(()=>{"use strict";window.onload=()=>{const e=document.querySelector("#createAccountButton"),t=document.querySelector("#createAccountNext"),c=document.querySelector("#createReceiverNext"),o=document.querySelectorAll("input"),r=document.querySelector(".auth");e.addEventListener("click",()=>{r.classList.remove("is-welcome"),r.classList.add("is-data")}),t.addEventListener("click",()=>{r.classList.remove("is-data"),r.classList.add("is-receiver")}),c.addEventListener("click",()=>{r.classList.remove("is-receiver")}),o.forEach(e=>{e.addEventListener("focus",()=>{document.querySelectorAll(".bottom").forEach(e=>{e.style.marginBottom="100%"})}),e.addEventListener("blur",()=>{document.querySelectorAll(".bottom").forEach(e=>{e.style.marginBottom="0"})})})}})();